mapper.select:
    select c.cust_id,c.cust_name,d3.dict_item_name cust_industry,d2.dict_item_name cust_source,d1.dict_item_name cust_level,c.cust_phone,c.cust_mobile
from customer c,base_dict d1,base_dict d2,base_dict d3
where c.cust_level = d1.dict_id and c.cust_source = d2.dict_id and c.cust_industry = d3.dict_id

    and c.cust_name like '%${custName}%'


      and d2.dict_id = #{custSource}


      and d3.dict_id = #{custIndustry}


      and d1.dict_id = #{custLevel}
